
import click
from api.models import db, User, Post
import random
import bcrypt

"""
In this file, you can add as many commands as you want using the @app.cli.command decorator
Flask commands are usefull to run cronjobs or tasks outside of the API but sill in integration 
with youy database, for example: Import the price of bitcoin every night as 12am
"""
def setup_commands(app):
     
    """ 
    This is an example command "insert-test-users" that you can run from the command line
    by typing: $ flask insert-test-users 5
    Note: 5 is the number of users to add
    """
    @app.cli.command("insert-test-users")
    def insert_test_users():
        avatar_urls = [
           "https://hips.hearstapps.com/hmg-prod/images/fotos-1533279584.jpg",
           "https://images.pexels.com/photos/2272853/pexels-photo-2272853.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
        ]

        test_users = [
            {"name": "Carlos", "surname": "Ramírez", "username": "carlos123"},
            {"name": "Ana", "surname": "López", "username": "ana456"},
            {"name": "Luis", "surname": "Martínez", "username": "luis789"},
            {"name": "María", "surname": "González", "username": "maria101"},
            {"name": "Pedro", "surname": "Pérez", "username": "pedro202"},
        ]

        plain_password = "12345"

        for user_data in test_users:
            password_in_bytes = bytes(plain_password, 'utf-8')

            salt = bcrypt.gensalt()
            hashed_password = bcrypt.hashpw(password_in_bytes, salt)

            new_user = User(
                avatar=random.choice(avatar_urls),
                name=user_data["name"],
                surname=user_data["surname"],
                username=user_data["username"],
                password=hashed_password.decode('utf-8')
            )

            db.session.add(new_user)

        db.session.commit()
        print(f"{len(test_users)} usuarios de prueba insertados exitosamente.")


    @app.cli.command("insert-post-data")
    def insert_test_data():
        post_images = [
            "https://images.pexels.com/photos/2272853/pexels-photo-2272853.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"
        ]

        post_messages = [
            "¡Qué día tan increíble en la playa!",
            "Disfrutando una buena taza de café ☕️",
            "Un amanecer espectacular 🌅",
            "Explorando nuevos lugares 🚀",
            "Recordando viejos tiempos 🎶"
        ]

        post_locations = [
            "Ciudad de México",
            "Buenos Aires",
            "Madrid",
            "Bogotá",
            "Santiago"
        ]
        users = User.query.all()

        num_posts = 10

        for _ in range(num_posts):
            author = random.choice(users)

            new_post = Post(
                image=random.choice(post_images),
                message=random.choice(post_messages), 
                author_id=author.id,
                location=random.choice(post_locations),
                status=0
            )

            db.session.add(new_post)

        db.session.commit()
        print(f"{num_posts} publicaciones de prueba insertadas exitosamente.")
        pass